const Image = require("../models/ImagePd");
const Product = require("../models/productModel");
const path = require("path");
const fs = require("fs");

// Upload ảnh
exports.uploadImage = async (req, res) => {
  try {
    const { productId } = req.body;
    if (!productId) return res.status(400).json({ message: "Cần có ID sản phẩm" });

    // Kiểm tra sản phẩm tồn tại
    const product = await Product.findById(productId);
    if (!product) return res.status(404).json({ message: "Không tìm thấy sản phẩm" });

    const image = new Image({
      product: productId,
      url: `/uploads/${req.file.filename}`,
    });

    await image.save();
    res.status(201).json({ message: "Ảnh tải lên thành công", image });
  } catch (err) {
    res.status(500).json({ message: err.message });
  }
};

// Lấy ảnh theo sản phẩm
exports.getImagesByProduct = async (req, res) => {
  try {
    const images = await Image.find({ product: req.params.productId });
    res.json(images);
  } catch (err) {
    res.status(500).json({ message: err.message });
  }
};

// Xóa ảnh
exports.deleteImage = async (req, res) => {
  try {
    const image = await Image.findById(req.params.id);
    if (!image) return res.status(404).json({ message: "Không tìm thấy ảnh" });

    // Xóa file vật lý
    const imagePath = path.join(__dirname, "..", image.url);
    fs.unlinkSync(imagePath);

    await Image.findByIdAndDelete(req.params.id);
    res.json({ message: "Ảnh đã được xóa" });
  } catch (err) {
    res.status(500).json({ message: err.message });
  }
};
