const express = require("express");
const router = express.Router();
const Motorcycle = require("../models/Motorcycle");

// CREATE: Thêm mới motorcycle
router.post("/", async (req, res) => {
  try {
    const motorcycle = new Motorcycle(req.body);
    await motorcycle.save();
    res.status(201).json(motorcycle);
  } catch (err) {
    res.status(400).json({ message: err.message });
  }
});

// READ: Lấy tất cả motorcycles
router.get("/", async (req, res) => {
  try {
    const motorcycles = await Motorcycle.find();
    res.status(200).json(motorcycles);
  } catch (err) {
    res.status(500).json({ message: err.message });
  }
});

// READ: Lấy một motorcycle theo ID
router.get("/:id", async (req, res) => {
  try {
    const motorcycle = await Motorcycle.findById(req.params.id);
    if (!motorcycle) {
      return res.status(404).json({ message: "Motorcycle not found" });
    }
    res.status(200).json(motorcycle);
  } catch (err) {
    res.status(500).json({ message: err.message });
  }
});

// UPDATE: Cập nhật thông tin motorcycle
router.put("/:id", async (req, res) => {
  try {
    const updatedMotorcycle = await Motorcycle.findByIdAndUpdate(req.params.id, req.body, { new: true });
    if (!updatedMotorcycle) {
      return res.status(404).json({ message: "Motorcycle not found" });
    }
    res.status(200).json(updatedMotorcycle);
  } catch (err) {
    res.status(400).json({ message: err.message });
  }
});

// DELETE: Xóa motorcycle
router.delete("/:id", async (req, res) => {
  try {
    const deletedMotorcycle = await Motorcycle.findByIdAndDelete(req.params.id);
    if (!deletedMotorcycle) {
      return res.status(404).json({ message: "Motorcycle not found" });
    }
    res.status(200).json({ message: "Motorcycle deleted successfully" });
  } catch (err) {
    res.status(400).json({ message: err.message });
  }
});

module.exports = router;
