+import React, { useEffect, useState } from "react";
+import { FaBoxOpen } from "react-icons/fa";
+
+export default function ProductItem({
+  name,
+  price,
+  description,
+  image,
+  amount,
+}) {
+  const [isValid, setValid] = useState(true);
+  const [currentImage, setCurrentImage] = useState(image?.[0] || "");
+  const [imageIndex, setImageIndex] = useState(0);
+
+  useEffect(() => {
+    if (!name || !price || !image || image.length === 0) {
+      setValid(false);
+    }
+  }, [name, price, image]);
+
+  const handleMouseEnter = () => {
+    if (image.length > 1) {
+      setImageIndex((prevIndex) => (prevIndex + 1) % image.length);
+      setCurrentImage(image[(imageIndex + 1) % image.length]);
+    }
+  };
+
+  const handleMouseLeave = () => {
+    setCurrentImage(image[0]);
+    setImageIndex(0);
+  };
+
+  return isValid ? (
+    <div
+      className="w-32 border p-1 rounded-2xl shadow-lg hover:shadow-xl transition-transform bg-neutral-800 backdrop-blur-md text-white snap-start hover:scale-105 duration-300"
+      onMouseEnter={handleMouseEnter}
+      onMouseLeave={handleMouseLeave}
+    >
+      <div className="w-full aspect-[3/2] overflow-hidden rounded-lg">
+        <img
+          src={currentImage}
+          alt={name}
+          loading="lazy"
+          decoding="async"
+          className="w-full h-full object-cover rounded-lg"
+        />
+      </div>
+
+      <div className="grid P-5 w-full mt-5">
+        <h2 className="text-lg font-semibold text-white drop-shadow-md">
+          {name}
+        </h2>
+        <p className="text-xl text-yellow-400 font-bold drop-shadow-md">
+          {Number(price).toLocaleString()}ƒë
+        </p>
+        <p className="text-gray-300 line-clamp-3 drop-shadow-sm">
+          {description || "Kh√¥ng c√≥ m√¥ t·∫£ s·∫£n ph·∫©m"}
+        </p>
+        <p className="flex items-center gap-2 text-green-300">
+          <FaBoxOpen className="text-green-400" />
+          {amount > 0 ? "C√≤n h√†ng" : "H·∫øt h√†ng"}
+        </p>
+      </div>
+    </div>
+  ) : (
+    <div className="text-red-500 p-4 bg-red-100 rounded-lg shadow-md">
+      S·∫£n ph·∫©m kh√¥ng h·ª£p l·ªá ho·∫∑c thi·∫øu th√¥ng tin.
+    </div>
+  );
+}

