import React from "react";
import PropTypes from "prop-types";

const Section = ({ title, children }) => {
  return (
    <div className="mb-8">
      <div className="relative inline-block bg-red-600 text-white font-bold px-4 py-2 uppercase ml-4">
        <span>{title}</span>
        <div className="absolute top-0 right-0 w-5 h-full bg-red-800 clip-triangle"></div>
      </div>
      <div className="mt-4">{children}</div>
    </div>
  );
};

Section.propTypes = {
  title: PropTypes.string.isRequired,
  children: PropTypes.node,
};

export default Section;
