import React from "react";

const Section = ({ title, children }) => {
  return (
    <div className="mb-8">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">{title}</h2>
      <div className="">{children}</div>
    </div>
  );
};

export default Section;
