+import { useCart } from "../context/CartContext";
+import { useState, useEffect } from "react";
+import axios from "axios";
+import Header from "../components/Header";
+import Footer from "../components/Footer";
+import { useRouter } from "next/router";
+
+const Home = () => {
+  const { cart, addToCart } = useCart(); // S·ª≠ d·ª•ng gi·ªè h√†ng t·ª´ Context
+  const [products, setProducts] = useState([]);
+  const [loading, setLoading] = useState(true);
+  const router = useRouter();
+
+  useEffect(() => {
+    const fetchProducts = async () => {
+      try {
+        const { data } = await axios.get("http://localhost:5000/api/products");
+        setProducts(data);
+      } catch (error) {
+        console.error("Failed to fetch products", error);
+      } finally {
+        setLoading(false);
+      }
+    };
+    fetchProducts();
+  }, []);
+
+  return (
+    <>
+      <Header />
+      <div className="container mx-auto p-4">
+        <h1 className="text-2xl font-bold mb-4">S·∫£n ph·∫©m ph·ª• t√πng xe m√°y Honda</h1>
+
+        {loading ? (
+          <p className="text-center text-gray-500">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
+        ) : products.length === 0 ? (
+          <p className="text-center text-gray-500">Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
+        ) : (
+          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
+            {products.map((product) => (
+              <div key={product._id} className="border p-4 rounded shadow hover:shadow-lg transition-shadow">
+                <img src={product.image} alt={product.name} className="w-full h-40 object-cover mb-2 rounded" />
+                <h2 className="text-xl font-semibold">{product.name}</h2>
+                <p className="text-gray-700 mb-2">Gi√°: {product.price.toLocaleString()} VND</p>
+                <button
+                  className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition-colors"
+                  onClick={() => addToCart(product)}
+                >
+                  Th√™m v√†o gi·ªè h√†ng
+                </button>
+              </div>
+            ))}
+          </div>
+        )}
+
+        <button
+          className="mt-4 px-4 py-2 bg-green-500 text-white rounded"
+          onClick={() => router.push("/cart")}
+        >
+          üõí Xem gi·ªè h√†ng ({cart.length})
+        </button>
+      </div>
+      <Footer />
+    </>
+  );
+};
+
+export default Home;

