import axios from "axios";
import { useState, useEffect } from "react";
import Header from "../components/Header";
import '../styles/globals.css';
import LandingPage from "../components/LandingPage";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const { data } = await axios.get("http://localhost:5000/api/products");
        setProducts(data);
      } catch (error) {
        console.error("Failed to fetch products", error);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const renderProducts = () => {
    if (loading) {
      return <p className="text-center text-gray-500">ƒêang t·∫£i s·∫£n ph·∫©m...</p>;
    }

    if (products.length === 0) {
      return <p className="text-center text-gray-500">Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>;
    }

    return (
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {products.map((product) => (
          <div
            key={product._id}
            className="border p-4 rounded shadow hover:shadow-lg transition-shadow"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-40 object-cover mb-2 rounded"
            />
            <h2 className="text-xl font-semibold">{product.name}</h2>
            <p className="text-gray-700 mb-2">
              Gi√°: {product.price.toLocaleString()} VND
            </p>
            <p className="text-sm text-gray-600 mb-4">{product.description}</p>
            <button className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition-colors">
              Th√™m v√†o gi·ªè h√†ng
            </button>
          </div>
        ))}
      </div>
    );
  };

  return (
    <>
      <Header />
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold mb-4">
          S·∫£n ph·∫©m ph·ª• t√πng xe m√°y Honda
        </h1>
        {renderProducts()}
      </div>
    </>
  );
}
